import React, { useState } from "react";
import "./Sidebar.css";

const categories = [
  {
    name: "Footwear",
    subcategories: [
      "Sports Shoes",
      "Casual Shoes",
      "Slippers & Flip Flops",
      "Sandals & Floaters",
      "Formal Shoes",
      "Loafers",
      "Sneakers",
      "Ethnic Footwear",
      "Shoe Accessories",
      "View All",
    ],
  },
  {
    name: "Bags & Luggage",
    subcategories: [
      "Backpacks",
      "Laptop Bags",
      "Hiking Bags",
      "Luggage & Suitcases",
      "Travel Accessories",
      "Office Bags",
      "View All",
    ],
  },
  {
    name: "Clothing",
    subcategories: [
      "T-Shirts & Polos",
      "Shirts",
      "Jeans",
      "Trousers & Chinos",
      "Innerwear & Sleepwear",
      "View All",
    ],
  },
  {
    name: "Winter Wear",
    subcategories: [
      "Jackets",
      "Sweatshirts",
      "Sweaters",
      "Thermals",
      "Sportswear",
      "T-Shirts & Polos",
      "Trackpants & Tracksuits",
      "View All",
    ],
  },
  {
    name: "Men's Grooming",
    subcategories: [
      "Shaving Creams & Gels",
      "Eyewear",
      "Sunglasses",
      "View All",
    ],
  },
  {
    name: "Watches",
    subcategories: [],
  },
  {
    name: "Jewellery & Cufflinks",
    subcategories: [],
  },
  {
    name: "Accessories",
    subcategories: [
      "Wallets",
      "Belts",
      "Hats & Caps",
      "Gift Sets",
      "Neckties & Cravats",
      "Card Holders",
      "Keychains",
      "Suspenders",
    ],
  },
];

const Sidebar = () => {
  const [hovered, setHovered] = useState(null);

  return (
    <div className="sidebar">
      {categories.map((cat, idx) => (
        <div
          className="category"
          key={idx}
          onMouseEnter={() => setHovered(idx)}
          onMouseLeave={() => setHovered(null)}
        >
          {cat.name}
          {hovered === idx && cat.subcategories.length > 0 && (
            <div className="subcategory">
              {cat.subcategories.map((sub, i) => (
                <div className="subitem" key={i}>
                  {sub}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Sidebar;
