import React, { useState, useEffect } from "react";
import "./Sidebar.css";

const categories = [
  { name: "Electronics", sub: ["Mobiles", "Laptops", "Cameras", "Headphones"] },
  { name: "Fashion", sub: ["Men", "Women", "Kids"] },
  { name: "Home & Kitchen", sub: ["Furniture", "Decor", "Appliances"] },
  { name: "Sports", sub: ["Fitness", "Outdoor", "Cycling"] },
  { name: "Books", sub: ["Fiction", "Non-Fiction", "Comics"] },
];

const Sidebar = () => {
  const [activeCategory, setActiveCategory] = useState(null);
  const [isTop, setIsTop] = useState(true);

  // Detect scroll to hide sidebar after scrolling down
  useEffect(() => {
    const handleScroll = () => {
      setIsTop(window.scrollY < 150); // show sidebar only if scrollY < 150
    };
    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className={`sidebar ${isTop ? "visible" : "hidden"}`}>
      {categories.map((cat, index) => (
        <div
          key={index}
          className="category"
          onMouseEnter={() => setActiveCategory(index)}
          onMouseLeave={() => setActiveCategory(null)}
        >
          <span>{cat.name}</span>
          {activeCategory === index && (
            <div className="subcategory">
              {cat.sub.map((subItem, idx) => (
                <div key={idx} className="subitem">
                  {subItem}
                </div>
              ))}
            </div>
          )}
        </div>
      ))}
    </div>
  );
};

export default Sidebar;
