import React, { useEffect, useState } from "react";
import Sidebar from "../assets";
import "./Home.css";

const Home = () => {
  const [showSidebar, setShowSidebar] = useState(true);

  // Hide sidebar when user scrolls down
  useEffect(() => {
    const handleScroll = () => {
      if (window.scrollY > 300) {
        setShowSidebar(false);
      } else {
        setShowSidebar(true);
      }
    };

    window.addEventListener("scroll", handleScroll);
    return () => window.removeEventListener("scroll", handleScroll);
  }, []);

  return (
    <div className="home-container">
      {showSidebar && <Sidebar />}
      <div className={`main-content ${showSidebar ? "with-sidebar" : "full-width"}`}>
        <h1>Welcome to Snapdeal Clone</h1>
        <p>Scroll down to see sidebar disappear ðŸ‘‡</p>
        <div style={{ height: "2000px", background: "#fafafa" }}>
          {/* Simulated product grid */}
        </div>
      </div>
    </div>
  );
};

export default Home;
